import { useState } from 'react';
import './UserForm.css';

const UserFrom = (props) => {

    const [enteredName, setEnteredName] = useState("");
    const [enteredAge, setEnteredAge] = useState("");

    const onChangeNameHandler = (event) => {
        setEnteredName(event.target.value);
        console.log(event.target.value);
    }

    const onChangeAgeHandler = (event) => {
        setEnteredAge(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const userData = {
            name: enteredName, age : {enteredAge}
        }

        props.onSaveUserData(userData);
    }



    return (
        <form  className="new-user__controls" onSubmit={submitHandler}>
            <div className='new-user__control'>
                <label>Username</label>
                <input type="text" onChange={onChangeNameHandler} value={enteredName}></input>
            </div>
            <div className='new-user__control'>
                <label>Age (Years)</label>
                <input type="number" onChange={onChangeAgeHandler} value={enteredAge}></input>
            </div>
            <div className='new-user__action'> 
                <button type="submit">Add User</button>
            </div>
        </form>
    );
}
export default UserFrom;
